import React from "react";
import CardVertical from "../../../components/websiteMobile/card/cardVertical/CardVertical";
import "./Body.css";

const Body = ()=>{
    let datos = [{title:"Pizza Peperoni", subtitle:"With tomato", img:"https://images.pexels.com/photos/4394612/pexels-photo-4394612.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"}, {title:"Pizza with chedar", subtitle:"With cheese", img:"https://images.pexels.com/photos/5108603/pexels-photo-5108603.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"}, {title:"Pizza Party", subtitle:"The Best Pizza", img:"https://images.pexels.com/photos/803290/pexels-photo-803290.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"}]
    return (
        <>
        <div className="bodyContainer">
            <CardVertical/>
            <CardVertical/>
        </div>
        <span className="bodyMore"><i class="far fa-chevron-double-down"></i>More<i class="far fa-chevron-double-down"></i></span>
        </>
    )
}
export default Body;

const orders = (array)=>{
    let order = [];
    let resul = [];
    for (let index = 0; index < array.length; index++) {
        const element = array[index];
        if(order.length < 2){
            order.push(element);
        } else
        {
            resul.push(order);
            order=[];
        }
    }
    return resul;
}
