import React from "react";
import "./CardHorizontal.css";
import {useDispatch} from "react-redux";
import { putCart } from "../../../../redux/action/cartGet";


const CardHorizontal = ({product})=>{
    const dispacht = useDispatch();

    const handleAdd = ()=>{
        dispacht(putCart({productId: product.id, cartId: product.orderLine.cartId, quantity: product.orderLine.quantity + 1}))
    };
    const  handleSubtract = ()=>{
        if(product.orderLine.quantity > 0){
            dispacht(putCart({productId: product.id, cartId: product.orderLine.cartId, quantity: product.orderLine.quantity - 1}))
        }
    };


    return(
        <div className="cardHorizontalContainer">
            <div className="imgContent">
                <img src={product.img} alt="" />
            </div>
            <div className="textContent">
                <h1>{product.name}</h1>
                <p>Lorem ipsum dolor sit.</p>
            </div>
            <div className="priceAndBtnContent">
                <span>${product.price}</span>
                <div>
                    <button onClick={handleAdd}><span>-</span></button> {product.orderLine.quantity}  <button onClick={handleSubtract}><span>+</span></button>
                </div>
            </div>
        </div>
    )
}

export default CardHorizontal;
